button.slick-prev,
button.slick-next {
  background: #fff;
  color: #fff;
  border: 2px solid #bdbdbd;
  width: 30px;
  height: 30px;
  overflow: hidden;
  border-radius: 50%;
}
.additional-vertical button.slick-prev {
  top:-20px;
  left: 50%;
  margin-left: -15px;
}
.additional-vertical button.slick-next {
  bottom: -20px;
  left: 50%;
  margin-left: -15px;
}
.products-carousel button.slick-prev,
.products-carousel button.slick-next,
.additional-horizontal button.slick-prev,
.additional-horizontal button.slick-next {
  top: 50%;
  margin-top: -15px;
}
.products-carousel button.slick-prev,
.additional-horizontal button.slick-prev {
  left: -10px;
}
.products-carousel button.slick-next,
.additional-horizontal button.slick-next {
  right: -10px;
}
button.slick-prev:before,
button.slick-next:before {
  width: 26px;
  height: 26px;
  left: 0;
  top: 0;
  line-height: 28px;
  text-align: center;
  content: '\e900';
  font-family: 'trueloaded';
  color: #bdbdbd;
  font-size: 26px;
  padding-right: 2px;
}
button.slick-prev:before {
  -ms-transform: rotate(90deg); /* IE 9 */
  -webkit-transform: rotate(90deg); /* Chrome, Safari, Opera */
  transform: rotate(90deg);
}
button.slick-next:before {
  -ms-transform: rotate(-90deg); /* IE 9 */
  -webkit-transform: rotate(-90deg); /* Chrome, Safari, Opera */
  transform: rotate(-90deg);
  line-height: 26px;
}
.products-carousel button.slick-prev:before,
.additional-horizontal button.slick-prev:before {
  -ms-transform: rotate(0deg); /* IE 9 */
  -webkit-transform: rotate(0deg); /* Chrome, Safari, Opera */
  transform: rotate(0deg);
}
.products-carousel button.slick-next:before,
.additional-horizontal button.slick-next:before {
  -ms-transform: rotate(-180deg); /* IE 9 */
  -webkit-transform: rotate(-180deg); /* Chrome, Safari, Opera */
  transform: rotate(-180deg);
}

