.sel_pr_values{overflow:hidden;}
.properties-table > li{font-size:16px;}
.single_banner{text-align:center;}


.catalog-breadcrumb li + li:before {
  content: '/';
}
.catalog-breadcrumb a,
.catalog-breadcrumb span {
  padding-right: 10px;
}
.catalog-breadcrumb li + li a,
.catalog-breadcrumb li + li span {
  padding-left: 10px;
}


.cart-box {
  position: relative;
  z-index: 100;
}
.cart-box > a {
  display: block;
  position: relative;
  z-index: 1;
}
.cart-box > a:before {
  content: '\e90d';
  font-family: trueloaded;
  font-size: 1.5em;
  line-height: 0.75;
  vertical-align: middle;
  display: inline-block;
}
.cart-box > a > span {
  display: inline-block;
  overflow: hidden;
  padding-left: 10px;
  vertical-align: top;
}
.cart-box > a > span > strong {
  display: block;
  font-weight: normal;
}
.cart-content {
  box-sizing: border-box;
  list-style: none;
}
.cart-content .item {
  display: block;
}
.cart-content .item:after {
  display: block;
  content: '';
  clear: both;
}
.cart-content .name {
  display: block;
}
.cart-content .image {
  float: left;
}
.cart-content .qty:after {
  content: '\274c';
  padding: 0 5px;
  font-size: .6em;
}
.cart-content .price {
  display: block;
  text-align: right;
}
.cart-content .cart-total {
  text-align: right;
}

.cart-box.hover-box .cart-content {
  width: 410px;
  position: absolute;
  display: none;
  top: 34px;
  right: -1px;
}
.cart-box.hover-box:hover .cart-content {
  display: block !important;
}
.functionality-bar .sort select {
  font-family: trueloaded, 'Hind', sans-serif;
  font-size: 14px;
}
.hover-box-content {
  background: #fff;
  box-shadow: 0 0 5px #ccc;
  width: 200px;
  padding: 10px;
  text-align: left;
}
.hover-box-content:before {
  background: #fff;
}
.qty_packs .qty-input{padding:0 0 20px;border-bottom:1px solid #ccc;}
.qty_packs .qty-input + .qty-input{padding:20px 0;}
.qty_packs .price_1, .qty_packs .qty-box{display:inline-block;vertical-align:top;}
.qty_packs .price_1:after{display:inline-block;vertical-align:top;content:'x';color:#bcbcbc;margin:0 25px 0 15px;font-size:18px;}
.qty_packs .price_1, .qty_packs .qty_price{font-size:18px;padding:3px 0 0;}
.pr_attr{display:block;font-size:14px;position:absolute;}
.qty_packs .qty_price{min-width:150px;display:inline-block;text-align:right;}

.cart-popup {
    width: 900px;
}

